package Arrays;

public class ArrayDemoDynamicaly {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int nums[] = new int[4];
		nums[0] = 2;
		nums[1] = 4;
		nums[2] = 6;
		nums[3] = 8;
		for(int i=0;i<4;i++) {
			System.out.println(nums[i]);
			
		}
		

	}

}
