package Loops;

public class WhileSample {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i = 1,j = 1;
		while(i<=4) {
			System.out.println(" Hii " + i);
			i++;
			while(j<=3) {
				System.out.println(" Hello " + j);
				j++;
				
				
			}
			
		}

	}

}
